import { useState, useEffect } from "react"; //This is a react hook
import "./App.css";

const App = () => {
  const [counter, setCounter] = useState(0);

  useEffect(() => {
    alert("You've changed the counter to" + counter)
  }, [counter]); //dependancy arrray, is second variable. makes counter 100 only at start

  return (
    <div className="App">
      <button
        onClick={() => {
          setCounter((prevCount) => prevCount - 1);
        }}
      >
        -
      </button>
      <h1>{counter}</h1>
      <button
        onClick={() => {
          setCounter((prevCount) => prevCount + 1);
        }}
      >
        +
      </button>
    </div>
  );
};

export default App;

